import { Component, OnInit } from '@angular/core';
import {
  FormBuilder,
  FormControl,
  FormGroup,
  Validators,
} from '@angular/forms';
import TaskModel from 'src/app/models/task-model';
import { TasksService } from 'src/app/services/tasks.service';

@Component({
  selector: 'app-task-form',
  templateUrl: './task-form.component.html',
  styleUrls: ['./task-form.component.css'],
})
export class TaskFormComponent implements OnInit {
  selectBorder = '';
  constructor(private taskService: TasksService, private fb: FormBuilder) {}
  myForm: FormGroup;
  newTask: TaskModel;

  ngOnInit(): void {
    this.myForm = this.fb.group({
      description: [null, [Validators.required]],
      customer_id: [null, [Validators.required]],
      isDone: [false, [Validators.required]],
      created: [null],
    });
  }

  onSubmit() {
    if (!this.myForm.value.customer_id) {
      this.selectBorder = '3px red solid';
      return;
    }
    const date = new Date(this.myForm.value.created)
      .toISOString()
      .split('.')[0];
    this.newTask = { ...this.myForm.value, created: date.replace('T', ' ') };
    this.taskService.create(this.newTask);
  }

  validate() {
    this.selectBorder = '';
  }
}
