import { Component, OnInit } from "@angular/core";
import { Router } from "@angular/router";
import { Observable } from "rxjs";
import { OperationsService } from "./services/operations.service";

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.css"],
})
export class AppComponent implements OnInit {
  isHome$: Observable<boolean>;
  isHome = false;
  btnValue = "Add Operation";
  constructor(private service: OperationsService, private router: Router) {}

  ngOnInit(): void {
    this.isHome$ = this.service.isHome$;
    this.service.isHome$.subscribe((res) => {
      this.btnValue = res ? "Add Operation" : "Home";
      this.isHome = res;
    });
  }

  nav() {
    this.router.navigate([this.isHome ? "/operation" : ""]);
    this.service.changePath();
  }
}
<div class="app">
  <app-header></app-header>
  <div class="button-container">
    <button (click)="nav()">All Operations</button>
    <button (click)="nav()">Add</button>
  </div>
  <router-outlet></router-outlet>
</div>
