import { Component, OnInit } from "@angular/core";
import { FormBuilder, FormGroup, Validators } from "@angular/forms";
import { ActivatedRoute } from "@angular/router";
import { Observable } from "rxjs";
import CustomerModel from "src/app/models/account-model";
import TaskModel from "src/app/models/operation-model";
import { AccountsService } from "src/app/services/accounts.service";
import { OperationsService } from "src/app/services/operations.service";

@Component({
  selector: "app-operation-form",
  templateUrl: "./operation-form.component.html",
  styleUrls: ["./operation-form.component.css"],
})
export class OperationFormComponent implements OnInit {
  accounts$: Observable<CustomerModel[]>;

  myForm: FormGroup = this.fb.group({
    description: [null, [Validators.required]],
    customer: [null, [Validators.required]],
    created: [null, [Validators.required]],
    is_done: [false],
  });
  newTask: TaskModel;
  isValid = true;
  errorMsg = "";
  paramsId = "";

  types: string[] = ["deposit", "withdraw", "loan"];

  constructor(
    private taskService: OperationsService,
    private accountsService: AccountsService,
    private fb: FormBuilder,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.accountsService.getAll();
    this.accounts$ = this.accountsService.accounts$;

    this.route.params.subscribe((res) => (this.paramsId = res["id"]));
    if (this.paramsId) {
      this.taskService.getById(this.paramsId).subscribe((res: TaskModel) => {
        this.myForm = this.fb.group({
          type: [res.type, [Validators.required]],
          created: [this.toValidDate(res.created), [Validators.required]],
          account: [res.account._id, [Validators.required]],
          interest: [res.interest],
          payments: [res.payments],
        });
      });
    }
  }

  onSubmit() {
    if (this.myForm.invalid) {
      this.isValid = false;
      return;
    }
    this.newTask = { ...this.myForm.value };
    if (this.paramsId) {
      this.taskService.update(this.paramsId, this.newTask);
      return;
    }
    this.taskService.create(this.newTask);
  }

  validate() {
    this.isValid = true;
  }

  toValidDate(date: string) {
    return new Date(date).toISOString().split(".")[0];
  }
}
<div class="form-container">
  <form [formGroup]="myForm" (ngSubmit)="onSubmit()">
    <h1>{{ paramsId ? "Edit Operation" : "Add New Operation" }}</h1>
    <span class="error-msg" *ngIf="!isValid">please fill all details</span>
    <div>
      <label>Type:</label>
      <select formControlName="type" (change)="validate()">
        <option [ngValue]="null" disabled>choose account</option>
        <option *ngFor="let item of accounts$ | async" [ngValue]="item._id">
          {{ item.number }}
        </option>
      </select>
    </div>
    <div>
      <label>account name:</label>
      <select formControlName="account" (change)="validate()">
        <option [ngValue]="null" disabled>choose account</option>
        <option *ngFor="let item of accounts$ | async" [ngValue]="item._id">
          {{ item.number }}
        </option>
      </select>
    </div>
    <div>
      <label>Created:</label>
      <input
        type="datetime-local"
        formControlName="created"
        (change)="validate()"
      />
    </div>
    <div>
      <label>Interest:</label>
      <input type="number" formControlName="interest" />
    </div>
    <div>
      <label>Payments:</label>
      <input type="number" formControlName="payments" />
    </div>
    <div class="submit-btn">
      <button>{{ paramsId ? "Save" : "Add" }}</button>
    </div>
  </form>
</div>
