import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { BehaviorSubject, Observable } from 'rxjs';
import TaskModel from '../models/task-model';

@Injectable({
  providedIn: 'root',
})
export class TasksService {
  URL = 'http://localhost:3700/api/tasks';

  private tasks: TaskModel[];
  private tasksSubject = new BehaviorSubject<TaskModel[]>([]);

  constructor(private http: HttpClient) {}

  getAll() {
    return this.http.get<TaskModel[]>(`${this.URL}/all`).subscribe((res) => console.log(res));

  }

  getById(id: number): Observable<TaskModel> {
    return this.http.get<TaskModel>(`${this.URL}/single/${id}`);
  }

  create(task: TaskModel): Observable<TaskModel> {
    return this.http.post<TaskModel>(`${this.URL}/all`, task);
  }

  update(id: number, task: TaskModel): Observable<void> {
    return this.http.put<void>(`${this.URL}/${id}`, task);
  }

  delete(id: number): Observable<void> {
    return this.http.delete<void>(`${this.URL}/${id}`);
  }
}
