import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-task-form',
  templateUrl: './task-form.component.html',
  styleUrls: ['./task-form.component.css']
})

export class TaskFormComponent implements OnInit{
  myForm:FormGroup;

  ngOnInit(): void {
    this.myForm = new FormGroup({
      description: new FormControl(''),
      customer: new FormControl(0),
      isDone: new FormControl(false),
    })
  }

  submit(describe:any, customer:any, isDone:any){
    console.log('here')
  }
}
<div class="form-container">
  <form [formGroup]="myForm">
    <h1>Add New Task</h1>
    <div>
      <label>Description:</label>
      <input type="text" class="describe" formControlName="description" />
    </div>
    <div>
      <label>Customer name:</label>
      <select formControlName="customer">
        <option value="1">tom</option>
        <option value="2">amit</option>
        <option value="3">cfir</option>
      </select>
    </div>
    <div>
      <label>Status:</label>
      <div><input type="checkbox" #isDone/> done</div>
    </div>
    <div class="submit-btn">
      <button>Add</button>
    </div>
  </form>
</div>
