import { Injectable, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import CustomerModel from '../models/customer-model';
import { BehaviorSubject } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class CustomersService implements OnInit {
  URL = 'http://localhost:3200/api/customers';

  private customerSubject = new BehaviorSubject<CustomerModel[]>([]);

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.getAll();
  }

  get customers$() {
    console.log(this.customerSubject.asObservable())
    return this.customerSubject.asObservable();
  }

  getAll() {
    this.http.get<CustomerModel[]>(`${this.URL}/all`).subscribe((res) => {
      this.customerSubject.next(res);
    });
  }
}
