import { Component, Input, OnInit } from '@angular/core';
import TaskModel from 'src/app/models/task-model';
import { TasksService } from 'src/app/services/tasks.service';

@Component({
  selector: 'app-single-task',
  templateUrl: './single-task.component.html',
  styleUrls: ['./single-task.component.css'],
})
export class SingleTaskComponent implements OnInit {
  navigateLink = '';
  constructor(private taskService: TasksService, private router:Rout) {}
  @Input() task: TaskModel | undefined;

  ngOnInit() {
    this.navigateLink = `/task/${this.task._id}`;
  }

  editClick() {
    this.taskService.setPath();
    [routerLink] = 'navigateLink';
  }

  onDelete() {
    this.taskService.delete(this.task._id);
  }
}
