import { Component, Input, OnInit } from '@angular/core';
import TaskModel from 'src/app/models/task-model';
import { TasksService } from 'src/app/services/tasks.service';

@Component({
  selector: 'app-single-task',
  templateUrl: './single-task.component.html',
  styleUrls: ['./single-task.component.css'],
})
export class SingleTaskComponent implements OnInit{

  navigateLink = ''
  constructor(private taskService: TasksService) {}
  @Input() task: TaskModel | undefined;

ngOnInit(){
  this.navigateLink = `/task/${this.task.id}`
}


  onDelete() {
    this.taskService.delete(this.task.id)
  }
}
<div class="task-box">
  <div class="task-box-header">
    <h2>{{ task?.description }}</h2>
    <div>
      <span class="delete" (click)="onDelete()">❌</span>
      <span class="edit" [routerLink]="['/task/']">✏️</span>
    </div>
  </div>
  <div>
    <div>
      <label>created:</label>
      <span>{{ task?.created | date }}</span>
    </div>
    <div>
      <label>customer:</label>
      <span>{{ task?.customer_id }}</span>
    </div>
    <div>
      <label>status:</label>
      <span>{{ task?.isDone ? "accomplished" : "not done" }}</span>
    </div>
  </div>
</div>
