import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { BehaviorSubject, Observable } from 'rxjs';
import TaskModel from '../models/task-model';
import { Router } from '@angular/router';
import { Location } from '@angular/common';

@Injectable({
  providedIn: 'root',
})
export class TasksService {
  URL = 'http://localhost:3700/api/tasks';
  isHome = true;

  private tasksSubject = new BehaviorSubject<TaskModel[]>([]);
  private isHomeSubject = new BehaviorSubject<boolean>(true);

  constructor(
    private http: HttpClient,
    private router: Router,
    private location: Location
  ) {
    this.getAll();
  }

  setPath() {
    this.isHomeSubject.next(this.location.path() === '');
  }

  get isHome$() {
    return this.isHomeSubject.asObservable();
  }

  get tasks$() {
    return this.tasksSubject.asObservable();
  }

  getAll() {
    this.http.get<TaskModel[]>(`${this.URL}/all`).subscribe((res) => {
      this.tasksSubject.next(res);
    });
  }

  getById(id: number): Observable<TaskModel> {
    return this.http.get<TaskModel>(`${this.URL}/single/${id}`);
  }

  create(task: TaskModel) {
    this.http.post<TaskModel>(`${this.URL}/all`, task).subscribe(() => {
      this.router.navigate(['/']);
    });
  }

  update(id: number, task: TaskModel): Observable<void> {
    return this.http.put<void>(`${this.URL}/${id}`, task);
  }

  delete(id: number) {
    this.http.delete<void>(`${this.URL}/${id}`).subscribe(() => {
      const newList = this.tasksSubject.value.filter((t) => t.id !== id);
      this.tasksSubject.next(newList);
    });
  }
}
