import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import TaskModel from 'src/app/models/task-model';
import { CustomersService } from 'src/app/services/customers.service';
import { TasksService } from 'src/app/services/tasks.service';

@Component({
  selector: 'app-task-form',
  templateUrl: './task-form.component.html',
  styleUrls: ['./task-form.component.css'],
})
export class TaskFormComponent implements OnInit {
  myForm: FormGroup = this.fb.group({
    description: [null, [Validators.required]],
    customer: [null, [Validators.required]],
    created: [null, [Validators.required]],
    is_done: [false],
  });
  newTask: TaskModel;
  isValid = true;
  errorMsg = '';
  paramsId = '';

  constructor(
    private taskService: TasksService,
    private customerService: CustomersService,
    private fb: FormBuilder,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.route.params.subscribe((res) => (this.paramsId = res['id']));
    if (this.paramsId) {
      this.taskService.getById(this.paramsId).subscribe((res) => {
        this.myForm = this.fb.group({
          description: [res.description, [Validators.required]],
          customer: [res.customer, [Validators.required]],
          created: [this.toValidDate(res.created), [Validators.required]],
          is_done: [res.is_done],
        });
      });
    }

    const allCustomers = this.customerService.getAll()
  }

  onSubmit() {
    if (this.myForm.invalid) {
      this.isValid = false;
      return;
    }
    this.myForm.value.created = this.toValidDate(this.myForm.value.created);
    this.newTask = { ...this.myForm.value };
    if (this.paramsId) {
      this.taskService.update(this.paramsId, this.newTask);
      return;
    }
    this.taskService.create(this.newTask);
  }

  validate() {
    this.isValid = true;
  }

  toValidDate(date: string) {
    return new Date(date).toISOString().split('.')[0];
  }
}
<div class="form-container">
  <form [formGroup]="myForm" (ngSubmit)="onSubmit()">
    <h1>{{ paramsId ? "Edit Task" : "Add New Task" }}</h1>
    <span class="error-msg" *ngIf="!isValid">please fill all details</span>
    <div>
      <label>Description:</label>
      <input
        type="text"
        formControlName="description"
        (keypress)="validate()"
      />
    </div>
    <div>
      <label>Customer name:</label>
      <select formControlName="customer" (change)="validate()">
        <option [ngValue]="null" disabled>choose customer</option>

        <!-- <option *ngFor="let item of list" [ngValue]="1">tom</option> -->
        <option [ngValue]="2">amit</option>
        <option [ngValue]="3">cfir</option>
      </select>
    </div>
    <div>
      <label>Created:</label>
      <input
        type="datetime-local"
        formControlName="created"
        (change)="validate()"
      />
    </div>
    <div>
      <label>Status:</label>
      <div><input type="checkbox" formControlName="is_done" /> done</div>
    </div>
    <div class="submit-btn">
      <button>{{ paramsId ? "Save" : "Add" }}</button>
    </div>
  </form>
</div>
