import { Component, Input, OnInit } from "@angular/core";
import { Router } from "@angular/router";
import OperationModel from "src/app/models/operation-model";
import { OperationsService } from "src/app/services/operations.service";

@Component({
  selector: "app-single-operation",
  templateUrl: "./single-operation.component.html",
  styleUrls: ["./single-operation.component.css"],
})
export class SingleOperationComponent implements OnInit {
  navigateLink = "";
  constructor(
    private operationService: OperationsService,
    private router: Router
  ) {}
  @Input() operation: OperationModel | undefined;

  ngOnInit() {
    this.navigateLink = `/operation/${this.operation._id}`;
  }

  editClick() {
    this.router.navigate([this.navigateLink]);
  }

  onDelete() {
    this.operationService.delete(this.operation._id);
  }
}
<div class="operation-box">
  <div class="operation-box-header">
    <h2>Account {{ operation?.account.number }}</h2>
    <div>
      <span class="delete" (click)="onDelete()">❌</span>
      <span class="edit" (click)="editClick()">✏️</span>
    </div>
  </div>
  <div>
    <div>
      <label>Created:</label>
      <span>{{ operation?.created | date }}</span>
    </div>
    <div>
      <label>Operation Type:</label>
      <span>{{ operation?.type }}</span>
    </div>
    <div>
      <label>Amount:</label>
      <span>{{ operation?.amount }} $</span>
    </div>
    <div>
      <label>Interest:</label>
      <span>{{ operation?.interest }} $</span>
    </div>
    <div>
      <label>Payments:</label>
      <span>{{ operation.type === "loan" ? operation?.payments : "-------" }}</span>
    </div>
  </div>
</div>
